GameLvL::GameLvL(sf::Sprite *sprite, int GameLvL) {
    this->s = new Sprite(*sprite);
    this->gameLvL = GameLvL;
    this->initMap();
}
GameLvL::~GameLvL() {
    cout << "============== removed from map lvl ============"<< endl;
    delete this->s;
    delete this->MapLvL;
}

